changeSet.sql:
GRANT SELECT, INSERT, UPDATE, DELETE ON ALL TABLES IN SCHEMA eventing TO order_flow;
GRANT USAGE, SELECT ON ALL SEQUENCES IN SCHEMA eventing TO order_flow;
GRANT SELECT, INSERT, UPDATE, DELETE ON ALL TABLES IN SCHEMA domain TO order_flow;
GRANT USAGE, SELECT ON ALL SEQUENCES IN SCHEMA domain TO order_flow;

rollback.sql:
REVOKE SELECT, INSERT, UPDATE, DELETE ON ALL TABLES IN SCHEMA eventing FROM order_flow;
REVOKE USAGE, SELECT ON ALL SEQUENCES IN SCHEMA eventing FROM order_flow;
REVOKE SELECT, INSERT, UPDATE, DELETE ON ALL TABLES IN SCHEMA domain FROM order_flow;
REVOKE USAGE, SELECT ON ALL SEQUENCES IN SCHEMA domain FROM order_flow;
